import React from "react";

function Js(){
    let curTime = new Date(2022,4,24,16);
    curTime = curTime.getHours();
    let greeting = "";
    const cssStyle ={};
    
    if(curTime >=1 && curTime < 12){
        greeting = "Good Morning";
        cssStyle.color= "green";
    
    }else if (curTime >=12 && curTime < 18){
        greeting = "Good Afternoon";
        cssStyle.color = "red"; 
    
    }else if (curTime >=18 && curTime < 24){
        greeting = "Good Night";
        cssStyle.color = "red";
    } 
    return
    
}

export default Js;
