import React,{useState} from "react";

const App = () => {

    const time = new Date().toLocaleTimeString();
    const [ctime,setCtime] = useState(time);

    const UpdateTime = () => {
        time = new Date().toLocaleTimeString();
        setCtime(time);
    };

    setInterval(UpdateTime,1000);

    return (
        <>
            <h1 className="heading_style">{ctime}</h1>
        </>
    )
}

export default App;
