    const IncNum = () => {
    setTime (ctime = new Date().toLocaleDateString)
    }
    return (
        <>
            <h1 className="heading_style"> {ctime} </h1>
            <button onClick={IncNum}> Click Here</button>
        </>
    )
}

export default App;
