import React,{useState} from "react";


const App = () => {
    const state = useState();
    const [count, setCount] = useState();

    const IncNum = () => {
    setCount (count + 1)
    }
    return (
        <>
            <h1 className="heading_style"> {count} </h1>
            <button onClick={IncNum}> Click Here</button>
        </>
    )
}

export default App;
